private fun addNewPlaylist(spotifyService: SpotifyService, user: String, name: String, description: String, songs: List<Int>, image: String) {
    spotifyService.addPlaylist(user, PlayListDraft(name, description, image, getSongsOf(spotifyService.getAllSongs(), songs)))
}

