private fun likes(spotifyService: SpotifyService, userId: String, listOfPlaylist: List<String>) {
    listOfPlaylist.forEach {
        spotifyService.addOrRemoveLike(userId, it)
    }
}

fun getSpotifyService(): SpotifyService {
    val spotifyService = SpotifyService(mutableListOf(), mutableListOf(), mutableListOf())
    addSongs(spotifyService)
    addUsers(spotifyService)
    addPlaylist(spotifyService)
    addLikes(spotifyService)
    return spotifyService
}